#include<bits/stdc++.h>
using namespace std;


int main(){

	string s="";
	
	auto collision = [&](int i,int j){
		vector<int> arr(26,-1);
		for(;i<=j;i++){
			int val=s[i]-'a';
			if(arr[val]!=-1) return arr[val];
			arr[val]=i;
		}
		return -1;
	}; 	
	
	auto solution= [&](string part,int n){
		int j=n-1;
		while(j<4095){
			int i=j-n+1;
			int ans=collision(i,j);
			if(ans==-1){
				cout<<part<<" "<<i+1<<" "<<j+1<<endl;
				return;
			}
			j=ans+n;
		}
	};

	solution("Part1",4);
	solution("Part2",14);

	return 0;
}